mapper.insert:
INSERT INTO mvc_product_tbl(
		pdNo
	  , pdName
	  , pdBrand
	  , pdImg
	  , pdCategory
	  , pdContent
	  , pdPrice
	  , pdQuantity
	  , pdStatus
	  , pdIndate)
VALUES(
	  (SELECT NVL(MAX(pdNo)+1, 1) 
	  FROM mvc_product_tbl)
	  , #{pdName}
	  , #{pdBrand}
	  , #{pdImg}
	  , #{pdCategory}
	  , #{pdContent}
	  , #{pdPrice}
	  , #{pdQuantity}
	  , #{pdStatus}
	  , sysdate)
